        public int DefaultPower
        {
            get
            {
                return defaultPower;
            }

            set
            {
                defaultPower = value;
            }
        }

        public int Power
        {
            get
            {
                return int.Parse(power.Text.ToString());
            }

            set
            {
                power.Text = value.ToString();
            }
        }

        public bool Selected
        {
            get
            {
                return selected;
            }

            set
            {
                selected = value;
                if (value)
                {
                    name.BackColor = Color.MediumSpringGreen;
                }
                else
                {
                    name.BackColor = Color.White;
                }
                OnSelectionChangedEvent(new EventArgs());
            }
        }

        public bool InHand
        {
            get
            {
                return inHand;
            }

            set
            {
                inHand = value;
            }
        }

        public new string Name
        {
            get
            {
                return name.Text;
            }

            set
            {
                name.Text = value;
            }
        }

        public new string Text
        {
            get
            {
                return text.Text;
            }

            set
            {
                text.Text = value;
            }
        }

        public AttackType Type
        {
            get
            {
                return type;
            }

            set
            {
                type = value;
            }
        }

        public int ID
        {
            get
            {
                return id;
            }

            set
            {
                id = value;
            }
        }

        public RarityType Rarity
        {
            get
            {
                return rarity;
            }

            set
            {
                if (value == RarityType.Common)
                {
                    power.BackColor = Color.AliceBlue;
                }
                else if (value == RarityType.Rare)
                {
                    power.BackColor = Color.DeepSkyBlue;
                }
                else if (value == RarityType.Epic)
                {
                    power.BackColor = Color.DarkOrchid;
                }
                else if (value == RarityType.Legendary)
                {
                    power.BackColor = Color.DarkOrange;
                }
                rarity = value;
            }
        }

        public enum AttackType
        {
            Null,
            Melee,
            Range,
            Melee_spy,
            Range_spy,
            Melee_weather,
            Range_weather,
            Clear_weather
        }

        public enum RarityType
        {
            Common,
            Rare,
            Epic,
            Legendary
        }

